package Controller;

import Model.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "Controller.CustomerServlet", urlPatterns = "/customer")
public class CustomerServlet extends HttpServlet {
    static List<Customer> customerList = new ArrayList<Customer>();

    static {
        customerList.add(new Customer(1, "Mai Van Hoan", "10/08/1992", "Ha Noi", "data:image/jpeg;base64,"));
        customerList.add(new Customer(2, "Nguyen Van Nam", "26/12/1991", "Ha Tinh", "data:image/jpeg;base64,"));
        customerList.add(new Customer(3, "Nguyen Thai Hoa", "1/6/1985", "Hue", "data:image/jpeg;base64,"));
        customerList.add(new Customer(4, "Tran Dang Khoa", "10/09/1991", "Da Nang", "data:image/jpeg;base64,"));
        customerList.add(new Customer(5, "Nguyen Dinh Thi", "13/09/1994", "Quang Nam", "data:image/jpeg;base64,"));
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

        request.setAttribute("customerList", customerList);
        request.getRequestDispatcher("customer.jsp").forward(request,response);

    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }
}
